INSERT INTO modules (id, name, description, order_index) VALUES
(1, 'Módulo 1: Transparencia y Acceso a la Información Ambiental', 'Evaluación de transparencia y acceso a información', 1),
(2, 'Módulo 2: Participación Ciudadana, Control Social y Toma de Decisiones Ambientales', 'Evaluación de participación ciudadana', 2),
(3, 'Módulo 3: Participación ciudadana en la toma de decisiones ambientales', 'Evaluación de participación en decisiones', 3);
INSERT INTO questions (id, module_id, question_text, question_type, order_index, recommendations) VALUES
(1, 1, 'Actualizo el menu transparencia y acceso a la información de manera mensual', 'statement', 1, '{"No": "Revise la normatividad vigente (Ley 1712 de 2014, resoluci\u00f3n 1519 de 2020, Acuerdo de Escaz\u00fa) y establezca ruta o plan de trabajo para dar cumplimiento. Sensibilice sobre el derecho de acceso a la informaci\u00f3n p\u00fablica e informaci\u00f3n ambiental. Inicie con la implementaci\u00f3n de la ruta o plan de trabajo.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Elabore lineamientos (protocolo, gu\u00eda, instructivo, procedimiento, etc) en el que se establezca los pasos y responsabilidades para la publicaci\u00f3n y actualizaci\u00f3n de la informaci\u00f3n conforme al art\u00edculo 9 de la Ley 1712 de 2014 y a la resoluci\u00f3n 1519 de 2020, asegurando que los contenidos est\u00e9n disponibles, actualizados y sean accesibles para la ciudadan\u00eda.", "S\u00ed - Avanzado": "Contin\u00fae con el proceso de validaci\u00f3n de la informaci\u00f3n y actualizaci\u00f3n en la p\u00e1gina web. Verifique periodicamente el cumplimiento de los requisitos establecidos en la resolucu\u00f3n 1519 de 2020. Adem\u00e1s de la actualizaci\u00f3n de la informaci\u00f3n, incorpore mejoras como el uso del lenguaje claro, formatos accesibles, traducci\u00f3n de informaci\u00f3n relevante a dialectos y lenguas del territorio, entre otros.", "S\u00ed - Intermedio": "Elabore el esquema de publicaci\u00f3n (uno de los instrumentos de la informaci\u00f3n obligatorios por la Ley 1712 de 2014). Si ya lo tiene revis\u00e9lo y actual\u00edcelo. Establezca sistema de revisi\u00f3n de la p\u00e1gina y alertas para verificar que la informaci\u00f3n se encuentra actualizada de manera oportuna."}'::json),
(2, 1, 'Actualizo el menu participa de manera mensual', 'statement', 2, '{"No": "Establezca un cronograma de actualizaci\u00f3n mensual del men\u00fa \"Participa\", de acuerdo con el Decreto 1499 de 2017, la resoluci\u00f3n 1519 de 2020, los lineamientos de Gobierno Digital y el Acuerdo de Escaz\u00fa, promoviendo mecanismos efectivos de participaci\u00f3n ciudadana.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Revise los requisitos que la resoluci\u00f3n 1519 de 2020 establece para el men\u00fa participa y realice los ajustes necesarios en la p\u00e1gina web para dar cumplimiento a los mismos. Socialice con las \u00e1reas responsables de promover la participaci\u00f3n y establezca criterios para la publicaci\u00f3n de la informaci\u00f3n correspondiente de manera oportuna.", "S\u00ed - Avanzado": "Contin\u00fae con la actualizaci\u00f3n periodica y oportuna de los espacios y dem\u00e1s acticvidades que promuevan la participaci\u00f3n (convocatorias, espacios de dialogo, talleres, informes, etc) Incorpore resultados, visibilizaci\u00f3n de compromisos, seguimiento, datos abiertos e informes en lenguaje claro. Permita espacios de rertoalimentaci\u00f3n.", "S\u00ed - Intermedio": "Establezca un sistema de actualizaci\u00f3n de la informaci\u00f3n (protocolo, procedimiento, instructivo, etc) en el que las \u00e1reas responsables de promover la participaci\u00f3n remitan de manera la informaci\u00f3n correspondiente para ser publicada en el men\u00fa participa. Socialice y capacite."}'::json),
(3, 1, 'Actualizo la información correspondiente a planes y programas de proyectos ambientales de manera mensual', 'statement', 3, '{"No": "Identifique los planes y programas de proyectos ambientales, organice la informaci\u00f3n de cada uno y realice la publicaci\u00f3n correspondiente.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Actualice mensualmente la informaci\u00f3n relacionada con planes, programas y proyectos ambientales, garantizando los principios de transparencia y m\u00e1xima publicidad consagrados en la Ley 1712 de 2014 y en el Acuerdo de Escaz\u00fa.", "S\u00ed - Avanzado": "Contin\u00fae con la publicaci\u00f3n de los planes y programas de los proyectos ambientales de manera mensual, mantega actualizada la informaci\u00f3n al respecto. Publique en formatos abiertos en concordancia con la Ley 1712 de 2014, las directrices del CONPES 3920 y del Acuerdo de Escaz\u00fa. Incluya seguimiento e indicadores de seguimiento.", "S\u00ed - Intermedio": "Revise periodicamente la totalidad de planes y proyectos ambientales, complete con la informaci\u00f3n que haga falta y actualice en su p\u00e1gina web. Presente la informaci\u00f3n de manera clara y comprensible."}'::json),
(4, 1, 'Mi página es accesible para todas las poblaciones diferenciales de mi territorio', 'statement', 4, '{"No": "Revise los criterios de accesibilidad establecidos en la resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 1 - Gu\u00eda de Accesibilidad de Contenidos Web - WCAG) y del art\u00edculo 14 del Decreto 2106 de 2019, y elabore una ruta o plan de trabajo que le permita iniciar con su implementaci\u00f3n de una manera racional.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Identifique los grupos poblaciones de su territorio y las necesidades de accesibilidad de cada uno. Inicie por identificar e incorporar criterios de accesibilidad y enfoque diferencial de los grupos poblacionales de su terrorio, elabor\u00e9 una ruta o plan de trabajo.", "S\u00ed - Avanzado": "Garantice que la p\u00e1gina web institucional cumpla con los est\u00e1ndares de accesibilidad definidos de la resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 1 -Gu\u00eda de Accesibilidad de Contenidos Web - WCAG) y del art\u00edculo 14 del Decreto 2106 de 2019, permitiendo el acceso efectivo de todas las poblaciones diferenciales del territorio. Incorpore acciones innovadoras que mejoren la accesibilidad, realice validadiciones con los grupo poblacionales de su territorio.", "S\u00ed - Intermedio": "Revise los criterios de accesibilidad establecidos en la resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 1 -Gu\u00eda de Accesibilidad de Contenidos Web - WCAG) y del art\u00edculo 14 del Decreto 2106 de 2019, identifique cu\u00e1les esta cumpliendo, cu\u00e1les tiene pendiente por implementar y que recursos necesitaria para ello. Elabore una ruta o plan de trabajo que le permita avanzar en su implementaci\u00f3n de una manera racional."}'::json),
(5, 1, 'He hecho pruebas de sistema de lenguaje claro en mi página', 'statement', 5, '{"No": "Inicie un diagn\u00f3stico b\u00e1sico de los contenidos publicados en la p\u00e1gina web institucional, especialmente aquellos relacionados con los derechos de acceso a la informaci\u00f3n ambiental. Como parte del compromiso con la transparencia, se sugiere aplicar los lineamientos de la Estrategia Nacional de Lenguaje Claro del Departamento Administrativo de la Funci\u00f3n P\u00fablica (DAFP), los cuales promueven el uso de un lenguaje accesible, directo y comprensible para toda la ciudadan\u00eda. Seg\u00fan La Ley 1712 de 2014, Acuerdo de Escaz\u00fa y Decreto 1499 de 2017.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Complemente las acciones realizadas con pruebas de comprensi\u00f3n aplicadas a ciudadanos y ciudadanas, centradas en contenidos claves como tr\u00e1mites, normativas o decisiones ambientales. Estas pruebas deben enfocarse en identificar dificultades en el entendimiento de la informaci\u00f3n y establecer ajustes que favorezcan la transparencia. Se sugiere emplear los instrumentos y gu\u00edas de la Estrategia Nacional de Lenguaje Claro (DAFP) como base metodol\u00f3gica y priorizar los contenidos que inciden directamente en el ejercicio del derecho al acceso a la informaci\u00f3n ambiental seg\u00fan La Ley 1712 de 2014, Acuerdo de Escaz\u00fa y Decreto 1499 de 2017.", "S\u00ed - Avanzado": "Consolide la experiencia institucional mediante la sistematizaci\u00f3n de buenas pr\u00e1cticas, enfocadas en la garant\u00eda del derecho de acceso a la informaci\u00f3n ambiental. Esta sistematizaci\u00f3n puede traducirse en manuales, protocolos o informes como parte de los indicadores de gesti\u00f3n y rendici\u00f3n de cuentas institucional. Seg\u00fan La Ley 1712 de 2014, Acuerdo de Escaz\u00fa y Decreto 1499 de 2017.", "S\u00ed - Intermedio": "Institucionalice la implementaci\u00f3n del lenguaje claro mediante protocolos que integren validaciones sistem\u00e1ticas con p\u00fablicos diversos, en especial aquellos con mayores barreras de acceso, como comunidades rurales, grupos \u00e9tnicos o personas con baja escolaridad. Este proceso debe alinearse con los principios y herramientas de la Estrategia Nacional de Lenguaje Claro (DAFP), asegurando la claridad en la comunicaci\u00f3n p\u00fablica y reforzando el acceso efectivo y no discriminatorio a la informaci\u00f3n ambiental. Seg\u00fan La Ley 1712 de 2014, Acuerdo de Escaz\u00fa y Decreto 1499 de 2017."}'::json),
(6, 1, 'Dentro de mi página web los canales de contacto son visibles y accesibles a todas las poblaciones diferenciales de mi territorio', 'statement', 6, '{"No": "Revise los criterios establecidos en el Decreto 1499 de 2017 (en lo referene al relacionamiento Estado-Ciudadano), la Ley 1712 de 2014, la resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 2), el Acuerdo de Escaz\u00fa y elabore una ruta o plan de trabajo que le permita iniciar con su implementaci\u00f3n de una manera racional.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Revise los criterios establecidos en el Decreto 1499 de 2017 (en lo referene al relacionamiento Estado-Ciudadano), la Ley 1712 de 2014, la resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 2) y el Acuerdo de Escaz\u00fa, y realice las adecuaciones necesarias para que sus canales de contacto sean visibles, accesibles y adaptados a las necesidades de las poblaciones diferenciales", "S\u00ed - Avanzado": "Realice seguimiento a los canales de contacto, asegure que se encuentren visibles, que sean accesibles y adaptados a las necesidades de las poblaciones diferenciales, en l\u00ednea con la Ley 1712 de 2014 y la Resolcu\u00f3n 1519 de 2020 (anexo t\u00e9cnico 2). Optimice los canales de contacto, revise formatos y valide accesibilidad.", "S\u00ed - Intermedio": "Revise los criterios de enfoque diferencial e incorpore a sus canales de contacto para garantizar la inclusi\u00f3n y participaci\u00f3n de los grupos poblaciones de su territorio."}'::json),
(7, 1, 'El tiempo de respuesta de nuestros PQRS ambientales siempre se entregan bajo los términos de ley', 'statement', 7, '{"No": "Elabore o revise el protocolo de atenci\u00f3n a PQRS e identifique las causas que impiden cumplir con los tiempos estipulados por la ley para entregar las respuestas. Implemente acciones de mejora.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Identifique las causas en los que se han presentados los incumplimientos e implemente acciones de mejora para evitar que vuelvan a presentarse.", "S\u00ed - Avanzado": "Realice seguimiento permanente y garantice que las respuestas a las PQRS ambientales se entreguen dentro de los t\u00e9rminos establecidos por la Ley 1755 de 2015, cumpliendo con los principios de legalidad, celeridad y eficacia en la gesti\u00f3n p\u00fablica. Publique un informe con estad\u00edsticas, tiemposde respuesta y mejora continua, de cuerdo a la resloci\u00f3n 1519 de 2020 (Anexo t\u00e9cnico 2).", "S\u00ed - Intermedio": "Analice los casos en los que no se han cumplido los terminos de ley y tome las acciones puntuales para evitar su nueva ocurrencia."}'::json),
(8, 1, 'Tengo establecidos claramente los criterios para denegar solicitudes de información ambiental', 'statement', 8, '{"No": "Revise los criterios establecidos en el art\u00edculo 18 de la Ley 1712 de 2014 y en el Acuerdo de Escaz\u00fa, respecto a los criterios, procedimientos, definiciones e instrumentos para la denegaci\u00f3n de la informaci\u00f3n y elabore una ruta o plan de trabajo que le permita avanzar y dar cumplimiento a la normatividad.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Elabore el \u00edndice de informaci\u00f3n clasificada y reservada (uno de los instrumentos de la informaci\u00f3n obligatorios por la Ley 1712 de 2014). Si ya lo tiene revis\u00e9lo y actual\u00edcelo. Defina claramente los criterios para denegar solicitudes de informaci\u00f3n ambiental, conforme a las causales previstas en el art\u00edculo 18 de la Ley 1712 de 2014, asegurando transparencia y motivaci\u00f3n adecuada.", "S\u00ed - Avanzado": "Mantenga el \u00edndice de informaci\u00f3n clasificada y reservada actualizado con los actos, documentos e informaciones calificados como clasificados o reservados, de conformidad la Ley 1712 de 2014 y al Acuerdo de Escaz\u00fa. El \u00edndice incluir\u00e1 sus denominaciones, la motivaci\u00f3n y la individualizaci\u00f3n del acto en que conste tal calificaci\u00f3n.", "S\u00ed - Intermedio": "Revise el \u00edndice de informaci\u00f3n clasificada y reservada, verifique que contegan claramente los criterios para denegar solicitudes de informaci\u00f3n ambiental, conforme a las causales previstas en el art\u00edculo 18 de la Ley 1712 de 2014, asegurando transparencia y motivaci\u00f3n adecuada. Verifique que se encuentra adoptado formalmente mediante acto administravo, actualice de ser necesario. Socialice y capacite tanto a servidores como a la ciudadan\u00eda respecto a las causales previstas en el art\u00edculo 18 de la Ley 1712 de 2014 para la denegaci\u00f3n de informaci\u00f3n."}'::json),
(9, 1, 'Conozco la prueba de interés público para determinar la denegación de información', 'statement', 9, '{"No": "Revise en el Acuerdo de Escaz\u00fa y en la Ley 1712 de 2024 los criterios de denegaci\u00f3n de informaci\u00f3n, entrega parcial de la informaci\u00f3n y la aplicaci\u00f3n de la prueba de inter\u00e9s p\u00fablico y establezca con claridad en qu\u00e9 casos procede su aplicaci\u00f3n. Estableza ruta o plan de trabajo para interiorizar y aplicar al interior de la entidad.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Revise en el Acuerdo de Escaz\u00fa y en la Ley 1712 de 2024 los criterios de denegaci\u00f3n de informaci\u00f3n, entrega parcial de la informaci\u00f3n y la aplicaci\u00f3n de la prueba de inter\u00e9s p\u00fablico. Determine en que casos y ocasiones procede la aplicaci\u00f3n de la prueba de inter\u00e9s p\u00fablico y elabore un protocolo (procedimiento, gu\u00eda, instrumento) que determine los criterios, roles y responsabilidades para su aplicaci\u00f3n. Socialice y capacite.", "S\u00ed - Avanzado": "Realice seguimiento periodico, mantega cifras actualizadas, documente los casos con an\u00e1lisis detallado (tipo de informaci\u00f3n, justificaci\u00f3n t\u00e9cnica y jur\u00eddica, tiempos, resultados) en los que tuvo que aplicar la prueba de inter\u00e9s p\u00fablico. Mantenga trazabilidad, gestione el conocimiento y publique la informaci\u00f3n.", "S\u00ed - Intermedio": "Realice seguimiento e identifique los casos en los que ha tenido que aplicar la prueba de inter\u00e9s p\u00fablico con sus debida justificacines (t\u00e9cnica y jur\u00eddica). Lleve indicadores al respecto."}'::json),
(10, 1, 'Cuento con formatos alternativos, accesibles y comprensibles para que la ciudadanía pueda acceder a la información ambiental y servicios de manera efectiva, incluyendo a la población en situación de vulnerabilidad.', 'statement', 10, '{"No": "Revise los criterios de accesibilidad para formatos, establecidos en la Ley 1712 de 2024, la Resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 1), el Acuerdo de Escaz\u00fa, la Ley 1346 de 2009 y los principios de accesibilidad universal y coteje frente a sus formatos y establezca una ruta o plan de trabajo para realizar la adecuaci\u00f3n de los formatos.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Dise\u00f1e formatos alternativos, accesibles y comprensibles que faciliten el acceso a la informaci\u00f3n ambiental y a los servicios institucionales, en cumplimiento de la Ley 1712 de 2024, la Resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 1), el Acuerdo de Escaz\u00fa, la Ley 1346 de 2009 y los principios de accesibilidad universal.", "S\u00ed - Avanzado": "Realice seguimiento al uso de sus formatos, verifique que accesibilidad seg\u00fan la poblaci\u00f3n objetivo, implemente mejoras y mantenga actualizados.", "S\u00ed - Intermedio": "Verifique que la totalidad de sus formatos sean alternativos, accesibles y comprensibles de acuerdo con los criterios establecidos en la Ley 1712 de 2024, la Resoluci\u00f3n 1519 de 2020 (anexo t\u00e9cnico 1), el Acuerdo de Escaz\u00fa, la Ley 1346 de 2009 y los principios de accesibilidad universal, y realice los ajustes correspondientes para facilitar el acceso a la informaci\u00f3n ambiental y a los servicios intitucionales."}'::json),
(11, 1, 'He definido costos asociados con la entrega de información ambiental razonables y transparentes', 'statement', 11, '{"No": "Revise la normatividad vigente (articulo 26 de la Ley 1712 de 2014, resoluci\u00f3n 1519 de 2020, Acuerdo de Escaz\u00fa) y establezca los costos de reproducci\u00f3n (razonables y transparentes) asociados a la entrega de informaci\u00f3n ambiental, evitando barreras econ\u00f3micas de acceso.", "No aplica": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "S\u00ed - B\u00e1sico": "Elabore y publique acto administrativo sobre costos de reproducci\u00f3n de informaci\u00f3n p\u00fablica, en el que se informe los costos de reproducci\u00f3n individualizado por costo unitario de los diferentes formatos a trav\u00e9s de los cuales se puede reproducir la informaci\u00f3n.", "S\u00ed - Avanzado": "Mantenga costos de reproduci\u00f3n publicados (con an\u00e1lisis basado en la normatividad), el acto admistrativo que los adopta actualizado, realice revisi\u00f3n anual participativa.", "S\u00ed - Intermedio": "Revise el acto administrativo sobre costos de reproducci\u00f3n y actualice, verifique que los costos definidos sean razonables y transparentes, basados en la normatividad vigente. Publique actualizaci\u00f3n e informe claramente sobre los costos de reproducci\u00f3n."}'::json),
(13, 2, 'Tengo un registro de emisiones y transferencias de contaminantes al aire, agua, suelo y subsuelo bajo su jurisdicción conforme a la normatividad aplicable', 'statement', 13, '{"No": {"text": "Se recomienda iniciar y mantener un registro actualizado de emisiones y transferencias de contaminantes al aire, agua, suelo y subsuelo, conforme a los lineamientos del RUA, el PRTR Colombia y la normativa ambiental vigente, como parte del cumplimiento del art\u00edculo 6 del", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Implementar un sistema b\u00e1sico de registro que permita comenzar la recolecci\u00f3n estructurada de datos sobre emisiones y transferencias de contaminantes, asegurando al menos un formato est\u00e1ndar para reportes internos.", "points": 1}, "S\u00ed - Avanzado": {"text": "Ampliar la interoperabilidad del sistema con otras bases de datos ambientales y asegurar el cumplimiento de est\u00e1ndares de calidad de datos, fortaleciendo capacidades t\u00e9cnicas del personal encargado.", "points": 3}, "S\u00ed - Intermedio": {"text": "Desarrollar un mecanismo de consolidaci\u00f3n y sistematizaci\u00f3n de los datos existentes, estableciendo procedimientos peri\u00f3dicos de actualizaci\u00f3n y validaci\u00f3n para mejorar la calidad del registro.", "points": 2}}'::json),
(16, 2, 'Mis datos ambientales siempre están disponibles en formatos editable', 'statement', 16, '{"No": {"text": "Se recomienda adoptar la pr\u00e1ctica de publicar los datos ambientales en formatos abiertos y editables (como .xls, .csv, .ods) en la p\u00e1gina web institucional y en plataformas de acceso p\u00fablico, asegurando que estos sean f\u00e1ciles de descargar, consultar y utilizar por la ciudadan\u00eda y otros actores interesados. Adem\u00e1s, se puede crear un sistema de alertas para notificar actualizaciones de los datos y garantizar su accesibilidad continua. Es importante tambi\u00e9n establecer un proceso claro para la revisi\u00f3n y carga peri\u00f3dica de nuevos datos, y proporcionar gu\u00edas de uso o tutoriales que faciliten la interpretaci\u00f3n y an\u00e1lisis de la informaci\u00f3n. Por \u00faltimo, se sugiere capacitar a los encargados del manejo de los datos para asegurar la calidad, consistencia y actualizaci\u00f3n de la informaci\u00f3n ambiental.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Se sugiere convertir los documentos PDF o formatos cerrados a formatos abiertos como Excel (XLS) o CSV, priorizando aquellos con mayor demanda o utilidad p\u00fablica.", "points": 1}, "S\u00ed - Avanzado": {"text": "Mejorar la documentaci\u00f3n de los conjuntos de datos, garantizando la inclusi\u00f3n de metadatos estandarizados (fecha de actualizaci\u00f3n, responsable, unidad de medida, etc.) y aseg\u00farate de mantenerlos actualizados.", "points": 3}, "S\u00ed - Intermedio": {"text": "Realizar un inventario de los conjuntos de datos existentes y establece un plan progresivo para liberar todos los datos en formatos abiertos, asegurando su actualizaci\u00f3n peri\u00f3dica.", "points": 2}}'::json),
(17, 2, 'Mido mi desempeño ambiental, implemento un plan estructurado, documentado para mitigar impactos y mejorar, así como divulgo los resultados a la ciudadanía', 'statement', 17, '{"No": {"text": "Se recomienda implementar un plan estructurado y documentado para medir el desempe\u00f1o ambiental institucional, mitigar impactos y mejorar continuamente. Este plan debe incluir indicadores de gesti\u00f3n, metas claras y acciones para la mitigaci\u00f3n de impactos, asegurando la transparencia y el cumplimiento de la normativa vigente. Los resultados deben ser divulgados de manera accesible a la ciudadan\u00eda, a trav\u00e9s de informes peri\u00f3dicos, plataformas digitales y otros medios de comunicaci\u00f3n, en coherencia con la Ley 152 de 1994 (Plan de Desarrollo) y el art\u00edculo 7 del Acuerdo de Escaz\u00fa. Esta pr\u00e1ctica no solo mejora la rendici\u00f3n de cuentas, sino que tambi\u00e9n fortalece la participaci\u00f3n ciudadana en la toma de decisiones ambientales.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Iniciar con la elaboraci\u00f3n de un diagn\u00f3stico ambiental b\u00e1sico que identifique los principales impactos, y formular un plan m\u00ednimo con metas y acciones prioritarias para empezar el monitoreo del desempe\u00f1o ambiental.", "points": 1}, "S\u00ed - Avanzado": {"text": "Fortalecer los canales de comunicaci\u00f3n con la ciudadan\u00eda para divulgar los resultados de forma accesible y comprensible, e incorporar espacios de retroalimentaci\u00f3n ciudadana.", "points": 3}, "S\u00ed - Intermedio": {"text": "Establecer un cronograma de implementaci\u00f3n y seguimiento del plan, con responsables definidos y rutinas de medici\u00f3n peri\u00f3dica para asegurar que los indicadores se apliquen de forma constante.", "points": 2}}'::json),
(18, 2, 'Publico información completa sobre los procesos de toma de decisiones ambientales ( concesiones, licencias, permisos, contratos, convenios o autorizaciones , entre otros)', 'statement', 18, '{"No": {"text": "Se recomienda publicar de manera accesible y completa la informaci\u00f3n sobre los procesos de toma de decisiones ambientales, tales como concesiones, licencias, permisos, contratos, convenios y autorizaciones. Esta informaci\u00f3n debe ser f\u00e1cilmente consultable en plataformas en l\u00ednea, en formatos abiertos y actualizados, y debe incluir detalles sobre los procedimientos, criterios de evaluaci\u00f3n, decisiones tomadas y los impactos ambientales considerados. Esta acci\u00f3n contribuye a garantizar la transparencia, el acceso a la informaci\u00f3n y la participaci\u00f3n ciudadana, en cumplimiento con los principios del art\u00edculo 6 del Acuerdo de Escaz\u00fa y la Ley 1712 de 2014.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Dise\u00f1ar un plan b\u00e1sico de publicaci\u00f3n peri\u00f3dica (ej., mensual) que incluya al menos un formato est\u00e1ndar para cada tipo de documento, asegurando que haya un punto de partida claro y accesible.", "points": 1}, "S\u00ed - Avanzado": {"text": "Incorporar una auditor\u00eda interna peri\u00f3dica que asegure la actualizaci\u00f3n constante de la informaci\u00f3n y facilite la interoperabilidad con otras plataformas p\u00fablicas o territoriales.", "points": 3}, "S\u00ed - Intermedio": {"text": "Implementar mecanismos de trazabilidad, como fichas t\u00e9cnicas por documento y cronogramas de publicaci\u00f3n, adem\u00e1s de completar la informaci\u00f3n faltante para brindar un panorama m\u00e1s coherente y verificable.", "points": 2}}'::json),
(20, 3, 'Tengo un plan ajustado a los marcos normativos nacionales e internacionales para asegurar la participación ciudadana en la toma de decisiones ambientales que incluye las obligaciones internacionales respecto a los derechos de los pueblos indígenas y las comunidades locales', 'statement', 20, '{"No": {"text": "Formule un plan de participaci\u00f3n ciudadana ajustado a los marcos normativos nacionales e internacionales, incluyendo las obligaciones sobre los derechos de los pueblos ind\u00edgenas y comunidades locales, conforme a el Acuerdo de Escaz\u00fa Y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Defina un plan completo para incorporar el enfoque \u00e9tnico-territorial y el cumplimiento de los marcos normativos nacionales e internacionales en participaci\u00f3n ambiental. Esto permitir\u00e1 garantizar procesos inclusivos, culturalmente adecuados y con reconocimiento de los derechos colectivos.", "points": 1}, "S\u00ed - Avanzado": {"text": "Comparta el plan como una buena pr\u00e1ctica institucional que d\u00e9 cumplimiento efectivo de La Ley 1757 de 2015 (Ley de Participaci\u00f3n) y El Acuerdo de Escaz\u00fa conforme a los est\u00e1ndares de participaci\u00f3n, transparencia y enfoque diferencial.", "points": 3}, "S\u00ed - Intermedio": {"text": "Mejore la implementaci\u00f3n del plan mediante acciones articuladas de consulta, seguimiento y formaci\u00f3n, en concordancia con la normativa nacional e instrumentos internacionales de derechos humanos y ambientales. Se sugiere asegurar que las medidas adoptadas reflejen los principios del Acuerdo de Escaz\u00fa y y La Ley 1757 de 2015 (Ley de Participaci\u00f3n). Especialmente en relaci\u00f3n con la participaci\u00f3n activa y la justicia ambiental para comunidades \u00e9tnicas.", "points": 2}}'::json),
(22, 3, 'Promuevo activamente que la ciudadanía participe en cualquier momento del proceso de toma de decisiones', 'statement', 22, '{"No": {"text": "Inicie acciones que permitan a la ciudadan\u00eda participar m\u00e1s all\u00e1 de las etapas formales de consulta, garantizando espacios abiertos y accesibles en el proceso de toma de decisiones ambientales, en l\u00ednea con el Acuerdo de Escaz\u00fa y conforme a La Ley 1757 de 2015 (Ley de Participaci\u00f3n)", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Mejore la difusi\u00f3n y accesibilidad de los canales existentes, promoviendo activamente su uso en diversas etapas del proceso de decisi\u00f3n. Esto permitir\u00e1 avanzar hacia una participaci\u00f3n m\u00e1s informada y continua, en l\u00ednea con los est\u00e1ndares del Acuerdo de Escaz\u00fa y La Ley 1757 de 2015(Ley de Participaci\u00f3n)", "points": 1}, "S\u00ed - Avanzado": {"text": "Proponga la participaci\u00f3n ciudadana como un componente transversal en todas las fases de decisi\u00f3n ambientales de su entidad, con enfoque de derechos, trazabilidad, y evaluaci\u00f3n participativa. conforme a La Ley 1757 de 2015 (Ley de Participaci\u00f3n) y a los principios de transparencia y justicia ambiental del Acuerdo de Escaz\u00fa.", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca la estrategia de participaci\u00f3n mediante contenidos pedag\u00f3gicos, convocatorias permanentes y mecanismos de retroalimentaci\u00f3n. Esto asegura mayor alcance y equidad en la participaci\u00f3n, conforme a La Ley 1757 de 2015 (Ley de Participaci\u00f3n) y a los principios de transparencia y justicia ambiental del Acuerdo de Escaz\u00fa.", "points": 2}}'::json),
(24, 3, 'Tengo planes específicos para facilitar la participación de las comunidades impactadas directamente en los procesos de toma de decisiones ambientales', 'statement', 24, '{"No": {"text": "Desarrolle planes espec\u00edficos para facilitar la participaci\u00f3n activa de las comunidades directamente impactadas por los procesos de toma de decisiones ambientales. Estos planes deben cumplir con los principios de acceso a la informaci\u00f3n y participaci\u00f3n efectiva establecidos en el Acuerdo de Escaz\u00fa y en del Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Ajuste los planes existentes para incluir acciones espec\u00edficas dirigidas a las comunidades m\u00e1s afectadas. Esto implica garantizar canales de comunicaci\u00f3n accesibles, tiempos adecuados para la participaci\u00f3n y facilitar la comprensi\u00f3n de los procesos, cumpliendo con el enfoque diferencial del Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n)", "points": 1}, "S\u00ed - Avanzado": {"text": "Consolide y documente estas pr\u00e1cticas como un referente institucional, promoviendo su sostenibilidad y alineaci\u00f3n continua con las normativas internacionales y los compromisos del Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n)", "points": 3}, "S\u00ed - Intermedio": {"text": "Se recomienda mejorar la implementaci\u00f3n de los planes, asegurando que los mecanismos de participaci\u00f3n sean efectivos, accesibles y culturalmente apropiados para las comunidades impactadas. Tambi\u00e9n se debe realizar un seguimiento a los resultados de la participaci\u00f3n, asegurando la retroalimentaci\u00f3n y cumplimiento de los compromisos adquiridos en base al Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n)", "points": 2}}'::json),
(26, 3, 'Tengo una estrategia para asegurar que las observaciones de la ciudadanía sean consideradas y contribuyan en los procesos de toma de decisiones', 'statement', 26, '{"No": {"text": "Establezca una estrategia b\u00e1sica que permita recopilar, organizar y considerar las observaciones ciudadanas dentro del proceso decisorio. Esto representa un primer paso hacia el cumplimiento de los est\u00e1ndares de participaci\u00f3n efectiva del Acuerdo de Escaz\u00fa La Ley 1757 de 2015 (Ley de Participaci\u00f3n)", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Fortalezca el proceso de recepci\u00f3n de observaciones incorporando mecanismos de trazabilidad interna. Esto permitir\u00e1 dar seguimiento a los aportes recibidos y avanzar hacia una participaci\u00f3n m\u00e1s transparente e incidente, dando cumplimiento a los principios del Acuerdo de Escaz\u00fa en materia de participaci\u00f3n y La Ley 1757 de 2015 (Ley de Participaci\u00f3n)", "points": 1}, "S\u00ed - Avanzado": {"text": "Proponga nuevas practicas, asegurando que todas las observaciones sean claramente reflejadas en las decisiones finales y que el sistema siga siendo accesible y evaluado de forma continua para garantizar la m\u00e1xima transparencia y efectividad en la participaci\u00f3n ciudadana.", "points": 3}, "S\u00ed - Intermedio": {"text": "Consolide la estrategia institucional para asegurar que las observaciones sean sistem\u00e1ticamente documentadas, analizadas y respondidas. Esto refuerza la rendici\u00f3n de cuentas y da cumplimiento a los principios del Acuerdo de Escaz\u00fa en materia de participaci\u00f3n y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 2}}'::json),
(28, 3, 'Facilito la participación de comunidades que hablen idiomas distintos al idioma oficial', 'statement', 28, '{"No": {"text": "Inicie el cumplimiento del Art\u00edculo 8 y 17 de la Ley 1712 de 2014 y del Art\u00edculo 6.6 del Acuerdo de Escaz\u00fa, adoptando medidas para garantizar el acceso a la informaci\u00f3n en idiomas, formatos y canales accesibles. Es clave adecuar los sistemas de informaci\u00f3n y medios de comunicaci\u00f3n para llegar efectivamente a grupos \u00e9tnicos, culturales y personas con discapacidad, asegurando su derecho a estar informados.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Fortalezca la accesibilidad de la informaci\u00f3n p\u00fablica mediante formatos alternativos, comprensibles y multiling\u00fces, conforme al Art\u00edculo 8 y 17 de la Ley 1712 de 2014. Igualmente, se deben adecuar los canales digitales y f\u00edsicos para facilitar el acceso a grupos \u00e9tnicos, culturales y personas con discapacidad. Esto en concordancia con el Art\u00edculo 6.6 del Acuerdo de Escaz\u00fa, que exige divulgaci\u00f3n en formatos accesibles y comprensibles. Mejorar estos aspectos permitir\u00e1 un acceso m\u00e1s equitativo y efectivo a la informaci\u00f3n ambiental.", "points": 1}, "S\u00ed - Avanzado": {"text": "Consolide la estrategia de accesibilidad mediante la divulgaci\u00f3n proactiva de informaci\u00f3n en idiomas y formatos alternativos, conforme al Art\u00edculo 8 y 17 de la Ley 1712 de 2014 y el Art\u00edculo 6.6 del Acuerdo de Escaz\u00fa. Se sugiere integrar mecanismos de evaluaci\u00f3n del impacto y asegurar que los sistemas de informaci\u00f3n sean inclusivos, alineados con el Programa de Gesti\u00f3n Documental, y permitan una participaci\u00f3n ciudadana efectiva.", "points": 3}, "S\u00ed - Intermedio": {"text": "Mejore la accesibilidad de la informaci\u00f3n p\u00fablica, cumpliendo con el Art\u00edculo 8 y 17 de la Ley 1712 de 2014 y el Art\u00edculo 6.6 del Acuerdo de Escaz\u00fa, garantizando su divulgaci\u00f3n en diversos idiomas y formatos accesibles. Adem\u00e1s, es necesario alinear los sistemas de informaci\u00f3n con los procedimientos establecidos y adaptarlos para grupos \u00e9tnicos, culturales y personas con discapacidad.", "points": 2}}'::json),
(30, 3, 'Proporciono y promuevo procedimientos administrativos y judiciales para que la ciudadanía pueda impugnar o recurrir en decisiones ambientales', 'statement', 30, '{"No": {"text": "Establezca rutas claras de acci\u00f3n jur\u00eddica y administrativa que permitan a la ciudadan\u00eda ejercer su derecho a impugnar decisiones ambientales. Esto responde al Acuerdo de Escaz\u00fa (art. 5) y a lo estipulado en la Ley 1757 de 2015 sobre mecanismos de participaci\u00f3n, as\u00ed como a la obligaci\u00f3n de garantizar el acceso efectivo a la informaci\u00f3n (Ley 1712 de 2014).", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Adopte mecanismos institucionales que garanticen el acceso a procedimientos administrativos y judiciales efectivos para impugnar decisiones ambientales, en cumplimiento del Art\u00edculo 5 del Acuerdo de Escaz\u00fa. Es fundamental que la ciudadan\u00eda conozca y acceda f\u00e1cilmente a estos recursos, conforme a la Ley 1757 de 2015 y a los principios de transparencia y acceso a la informaci\u00f3n de la Ley 1712 de 2014.", "points": 1}, "S\u00ed - Avanzado": {"text": "Consolide las buenas pr\u00e1cticas institucionales, como la promoci\u00f3n activa del acceso a la justicia ambiental, mediante estrategias de divulgaci\u00f3n, acompa\u00f1amiento jur\u00eddico y evaluaci\u00f3n del impacto de los recursos ejercidos. Esto debe estar articulado con el Art\u00edculo 85del Acuerdo de Escaz\u00fa, promoviendo justicia ambiental efectiva, accesible y equitativa, en l\u00ednea con las leyes 1712 y 1757.", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca y visibilice los procedimientos ya existentes, garantizando que la ciudadan\u00eda conozca sus derechos y c\u00f3mo ejercerlos frente a decisiones ambientales. Se debe avanzar en la eliminaci\u00f3n de barreras de acceso (territoriales, digitales o jur\u00eddicas) y cumplir con el Acuerdo de Escaz\u00fa (Art. 8), asegurando una participaci\u00f3n activa y justa, conforme a la Ley 1757 y la Ley 1712.", "points": 2}}'::json),
(31, 3, 'Promuevo activamente el ejercicio del derecho al control social sobre la gestión ambiental', 'statement', 31, '{"No": {"text": "Elabor\u00e9 e implemente un plan espec\u00edfico para fomentar el control social activo en la gesti\u00f3n ambiental, creando espacios formales de participaci\u00f3n donde la ciudadan\u00eda pueda monitorear y evaluar las decisiones ambientales. Adem\u00e1s, debe garantizar que la informaci\u00f3n relevante sea proporcionada de manera accesible y en formatos comprensibles para facilitar la participaci\u00f3n, revise T\u00edtulo V cap\u00edtulo 1 Del control social a lo p\u00fablico de la Ley 1757.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Implemente mecanismos claros para promover el ejercicio del control social en la gesti\u00f3n ambiental, en l\u00ednea con los principios de transparencia y participaci\u00f3n ciudadana establecidos en la Ley 1712 de 2014 y la Ley 1757 de 2015. Adem\u00e1s, debe dar acceso a la informaci\u00f3n p\u00fablica y garantizar la participaci\u00f3n efectiva de la ciudadan\u00eda, tal como lo estipula el Art\u00edculo 8 del Acuerdo de Escaz\u00fa.", "points": 1}, "S\u00ed - Avanzado": {"text": "Expanda las estrategias de control social, promoviendo la participaci\u00f3n activa y continua de la ciudadan\u00eda en la gesti\u00f3n ambiental, m\u00e1s alla de los procesos de rendici\u00f3n de cuenta, atrevase a implementar otro tipo de modalidades de control social como veedur\u00edas ciudadanas, las Juntas de vigilancia, los Comit\u00e9s de Desarrollo y Control Social de los Servicios P\u00fablicos Domiciliarios, las auditor\u00edas ciudadanas y las instancias de participaci\u00f3n ciudadana, revise articulos 63 y 65", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca los mecanismos de participaci\u00f3n ciudadana para el ejercicio del control social sobre la gesti\u00f3n ambiental, promoviendo un acceso amplio y transparente a la informaci\u00f3n. Esto debe alinearse con la la Ley 1757 de 2015, y el Art\u00edculo 7 del Acuerdo de Escaz\u00fa, garantizando que las decisiones y acciones ambientales sean sujetas a supervisi\u00f3n y rendici\u00f3n de cuentas por parte de la ciudadan\u00eda.", "points": 2}}'::json),
(32, 3, 'Tengo un canal formal exclusivo para recibir y atender solicitudes de información pública relacionadas con el control social ambiental', 'statement', 32, '{"No": {"text": "Establezca un canal formal de participaci\u00f3n que permita a la ciudadan\u00eda acceder a la informaci\u00f3n y expresar sus opiniones sobre la gesti\u00f3n ambiental, en cumplimiento con el Art. 8 de la Ley 1712 de 2014 y el Acuerdo de Escaz\u00fa. Este canal debe garantizar la accesibilidad y estar alineado con los principios de participaci\u00f3n activa y transparencia establecidos en la Ley 1757 de 2015. Adem\u00e1s, se debe asegurar un seguimiento adecuado y la implementaci\u00f3n de formatos accesibles para grupos vulnerables. Esto iniciar\u00e1 el fortalecimiento del control social ambiental.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Fortalezca el canal existente haci\u00e9ndolo m\u00e1s visible y accesible, utilizando formatos comprensibles para todos los grupos (Art. 8 Ley 1712 de 2014), aline\u00e1ndolo con el Acuerdo de Escaz\u00fa (Arts. 5 y 6) para garantizar inclusi\u00f3n y participaci\u00f3n efectiva. Adem\u00e1s, es clave establecer protocolos claros de atenci\u00f3n y respuesta oportuna, promoviendo as\u00ed un ejercicio m\u00e1s robusto del control social ambiental.", "points": 1}, "S\u00ed - Avanzado": {"text": "Consolide el canal formal de participaci\u00f3n, garantizando su alineaci\u00f3n con los principios de la Ley 1757 de 2015 sobre participaci\u00f3n efectiva y acceso a la informaci\u00f3n. Este canal debe integrarse con los sistemas de gesti\u00f3n documental y cumplir con los criterios de accesibilidad establecidos en la Ley 1712 de 2014, as\u00ed como los principios del Acuerdo de Escaz\u00fa. Adem\u00e1s, se sugiere implementar plataformas tecnol\u00f3gicas para el seguimiento de solicitudes y generar retroalimentaci\u00f3n continua, optimizando el control social ambiental y asegurando la transparencia y eficiencia del proceso.", "points": 3}, "S\u00ed - Intermedio": {"text": "Mejore el canal formal garantizando su visibilidad y accesibilidad (Art. 8 Ley 1712 de 2014), facilitando el ejercicio efectivo del control social ambiental. Debe alinearse con el Acuerdo de Escaz\u00fa, asegurando informaci\u00f3n oportuna y comprensible. Es clave incluir mecanismos de retroalimentaci\u00f3n ciudadana. As\u00ed se fortalece la participaci\u00f3n y transparencia en la gesti\u00f3n ambiental.", "points": 2}}'::json),
(34, 3, 'Uso la información de la evaluación ciudadana de la rendición de cuentas para la mejora de mi gestión ambiental', 'statement', 34, '{"No": {"text": "Implemente de inmediato un mecanismo para recoger y utilizar la retroalimentaci\u00f3n ciudadana proveniente de la rendici\u00f3n de cuentas. La falta de este proceso limita la capacidad de la entidad para responder de manera efectiva a las preocupaciones y sugerencias de la ciudadan\u00eda, lo que afecta la transparencia y la legitimidad de las decisiones ambientales. Utilizar esta informaci\u00f3n para ajustar las pol\u00edticas y acciones ambientales fortalecer\u00e1 el compromiso con la gesti\u00f3n p\u00fablica y la participaci\u00f3n de la comunidad.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Utilice la informaci\u00f3n obtenida de la evaluaci\u00f3n ciudadana en el proceso de rendici\u00f3n de cuentas para identificar \u00e1reas de mejora en la gesti\u00f3n ambiental. Es importante estructurar un sistema sencillo que permita recopilar y organizar los comentarios y sugerencias de la ciudadan\u00eda. Esta retroalimentaci\u00f3n debe ser tomada en cuenta para ajustar de manera progresiva las pol\u00edticas y acciones ambientales de la entidad.", "points": 1}, "S\u00ed - Avanzado": {"text": "Integre un sistema formal y sistem\u00e1tico para el uso de la informaci\u00f3n obtenida de la evaluaci\u00f3n ciudadana en la rendici\u00f3n de cuentas, asegurando que esta retroalimentaci\u00f3n se utilice para la mejora continua de la gesti\u00f3n ambiental. La entidad debe implementar herramientas tecnol\u00f3gicas para analizar la informaci\u00f3n y priorizar \u00e1reas de mejora, ajustando las pol\u00edticas y estrategias ambientales de manera \u00e1gil. Adem\u00e1s, se debe garantizar la transparencia en c\u00f3mo se incorporan estas sugerencias y se comunican los cambios realizados a la ciudadan\u00eda.", "points": 3}, "S\u00ed - Intermedio": {"text": "Formalice el proceso de retroalimentaci\u00f3n ciudadana tras la rendici\u00f3n de cuentas, utilizando la informaci\u00f3n obtenida para realizar ajustes estrat\u00e9gicos en la gesti\u00f3n ambiental. Se sugiere establecer mecanismos claros para incorporar las sugerencias y cr\u00edticas en la planificaci\u00f3n y ejecuci\u00f3n de proyectos ambientales, mejorando la transparencia y eficiencia. Esto debe ser acompa\u00f1ado de un seguimiento para asegurar que las acciones de mejora sean implementadas efectivamente.", "points": 2}}'::json),
(35, 3, 'Diseño diálogos de rendición de cuentas para la toma de desiciones ambientales', 'statement', 35, '{"No": {"text": "Debe iniciar el dise\u00f1o de los di\u00e1logos de rendici\u00f3n de cuentas, asegurando su alineaci\u00f3n con la Ley 1712 de 2014, el Acuerdo de Escaz\u00fa y la Ley 1757 de 2015. Estos di\u00e1logos deben ser estructurados de manera accesible, utilizando tecnolog\u00edas inclusivas y garantizando que los resultados se utilicen para ajustar las decisiones ambientales. Es esencial establecer un proceso continuo de participaci\u00f3n ciudadana para mejorar la transparencia y la toma de decisiones.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Fortalezca los di\u00e1logos de rendici\u00f3n de cuentas alineados con la Ley 1712 de 2014 y el Acuerdo de Escaz\u00fa, asegurando que sean m\u00e1s accesibles y utilizados para ajustar las decisiones ambientales. Adem\u00e1s, debe garantizar la participaci\u00f3n ciudadana conforme al art\u00edculo 8 de la Ley 1757 de 2015, promoviendo la transparencia y el acceso a la informaci\u00f3n.", "points": 1}, "S\u00ed - Avanzado": {"text": "Optimice los di\u00e1logos de rendici\u00f3n de cuentas, aline\u00e1ndolos de manera integral con la Ley 1712 de 2014, el Acuerdo de Escaz\u00fa y la Ley 1757 de 2015, asegurando que no solo se d\u00e9 acceso a la informaci\u00f3n, sino que los resultados sean utilizados para ajustar pol\u00edticas ambientales. Adem\u00e1s, debe aplicar herramientas tecnol\u00f3gicas avanzadas que permitan la inclusi\u00f3n de diversas poblaciones y proporcionar espacios de retroalimentaci\u00f3n continua para mejorar la toma de decisiones.", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca los di\u00e1logos de rendici\u00f3n de cuentas, garantizando su accesibilidad y aline\u00e1ndolos con la Ley 1712 de 2014 y el Acuerdo de Escaz\u00fa, para asegurar una participaci\u00f3n ciudadana efectiva. Asimismo, debe asegurarse de que los resultados de estos di\u00e1logos se utilicen en la toma de decisiones ambientales, conforme al art\u00edculo 8 de la Ley 1757 de 2015, promoviendo una gesti\u00f3n m\u00e1s transparente y responsable.", "points": 2}}'::json),
(36, 3, 'Describa las principales fortalezas de su entidad en materia de transparencia, participación y evaluación ambiental', 'open', 36, '{"general": {"text": "Sistematice y documente las fortalezas identificadas como buenas pr\u00e1cticas institucionales. Utilice esta informaci\u00f3n para fortalecer los procesos existentes y compartir experiencias con otras entidades del territorio.", "points": 0}}'::json),
(37, 3, 'Identifique los principales retos y oportunidades de mejora en la implementación de los derechos de acceso en asuntos ambientales', 'open', 37, '{"general": {"text": "Elabore un plan de mejoramiento institucional basado en los retos identificados, estableciendo metas, responsables y cronogramas espec\u00edficos para avanzar en la implementaci\u00f3n efectiva de los derechos de acceso en asuntos ambientales.", "points": 0}}'::json),
(12, 2, 'Informo a mi ciudadanía de manera trimestral sobre su derecho al acceso de la información ambiental (por medios físicos, remotos y locales de comunicación)', 'statement', 12, '{"No": {"text": "Profundizar la evaluaci\u00f3n de impacto con indicadores cualitativos y cuantitativos, y replicar la estrategia en otros territorios o entidades como buena pr\u00e1ctica institucional, promoviendo su adaptaci\u00f3n a contextos diversos.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Dise\u00f1ar un plan b\u00e1sico de divulgaci\u00f3n trimestral que defina responsables, medios disponibles (como carteleras, boletines o redes sociales) y un cronograma m\u00ednimo para iniciar la comunicaci\u00f3n regular sobre el derecho al acceso a la informaci\u00f3n ambiental.", "points": 1}, "S\u00ed - Avanzado": {"text": "Implementar mecanismos de retroalimentaci\u00f3n ciudadana para verificar la comprensi\u00f3n y utilidad de la informaci\u00f3n difundida, e incorporar ajustes basados en estos insumos para mejorar la efectividad de la estrategia.", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalecer la estrategia mediante la estandarizaci\u00f3n y calendarizaci\u00f3n de las acciones de comunicaci\u00f3n, asegurando que la informaci\u00f3n llegue de forma constante a diferentes grupos poblacionales mediante una combinaci\u00f3n de medios f\u00edsicos y digitales.", "points": 2}}'::json),
(14, 2, 'Garantizo la divulgación inmediata de información en caso de una amenaza inminente a la salud pública o al medio ambiente', 'statement', 14, '{"No": {"text": "Se recomienda establecer un protocolo para la divulgaci\u00f3n inmediata de informaci\u00f3n ante una amenaza inminente a la salud p\u00fablica o al ambiente, en cumplimiento del principio de precauci\u00f3n, el art\u00edculo 8 de la Ley 99 de 1993 y los compromisos del art\u00edculo 6 del Acuerdo de Escaz\u00fa. Este protocolo puede incluir la activaci\u00f3n de alertas tempranas a trav\u00e9s de medios comunitarios, redes sociales institucionales, mensajes de texto masivos, radios locales y carteleras f\u00edsicas en zonas afectadas. Tambi\u00e9n es clave capacitar al personal responsable y articularse con los comit\u00e9s de gesti\u00f3n del riesgo y autoridades ambientales para asegurar una respuesta r\u00e1pida, coordinada y efectiva.", "points": 0}, "No aplica": {"text": "Revisar competencias teniendo en cuenta la Ley 99 de 1993 y los compromisos del art\u00edculo 6 del Acuerdo de Escaz\u00fa.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Dise\u00f1ar e implementar un protocolo b\u00e1sico de divulgaci\u00f3n de informaci\u00f3n para emergencias ambientales, definiendo roles institucionales, canales de comunicaci\u00f3n y tiempos de respuesta.", "points": 1}, "S\u00ed - Avanzado": {"text": "Fortalecer la divulgaci\u00f3n mediante capacitaci\u00f3n continua del personal y retroalimentaci\u00f3n de la ciudadan\u00eda para mejorar los mensajes y canales utilizados.", "points": 3}, "S\u00ed - Intermedio": {"text": "Establecer un comit\u00e9 interinstitucional de respuesta r\u00e1pida para coordinar acciones de divulgaci\u00f3n inmediata, reduciendo tiempos y evitando duplicidades de informaci\u00f3n.", "points": 2}}'::json),
(15, 2, 'Mi entidad usa y carga información de los sistemas de información ambiental (VITAL, SIAC, RUA)', 'statement', 15, '{"No": {"text": "Se recomienda fortalecer el uso y la actualizaci\u00f3n oportuna de los sistemas de informaci\u00f3n ambiental como VITAL, SIAC y RUA, asegurando la carga regular de datos conforme a la normativa vigente. Esto mejora la gesti\u00f3n ambiental, facilita la toma de decisiones basada en evidencia y contribuye al cumplimiento de los principios de transparencia y acceso a la informaci\u00f3n establecidos en el Acuerdo de Escaz\u00fa. Adem\u00e1s, se sugiere capacitar al personal t\u00e9cnico y designar responsables para garantizar el funcionamiento continuo de estos sistemas.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Realizar una jornada de sensibilizaci\u00f3n y capacitaci\u00f3n sobre el funcionamiento y la importancia de los sistemas VITAL, SIAC y RUA, dirigida a los equipos t\u00e9cnicos y administrativos. Esto ayudar\u00e1 a reconocer los sistemas y su relevancia institucional.", "points": 1}, "S\u00ed - Avanzado": {"text": "Revisar y actualizar los procedimientos internos para mejorar la calidad de los datos cargados, asegurando la retroalimentaci\u00f3n con los equipos t\u00e9cnicos y fortaleciendo la rendici\u00f3n de cuentas sobre los reportes generados.", "points": 3}, "S\u00ed - Intermedio": {"text": "Establecer un cronograma institucional con responsables definidos para la carga peri\u00f3dica de informaci\u00f3n, e implementar un protocolo b\u00e1sico de validaci\u00f3n de datos antes del env\u00edo.", "points": 2}}'::json),
(19, 2, 'Tengo una estrategia dirigida a privados, presentes en mi territorio, para que publiquen información ambiental', 'statement', 19, '{"No": {"text": "Se recomienda desarrollar una estrategia dirigida a los actores privados presentes en el territorio, con el fin de promover la publicaci\u00f3n de informaci\u00f3n ambiental relevante. Esto puede incluir la creaci\u00f3n de alianzas con empresas, asociaciones y organizaciones locales, brindando orientaci\u00f3n sobre la importancia de la transparencia ambiental y c\u00f3mo cumplir con las normativas vigentes. Adem\u00e1s, se pueden establecer incentivos o compromisos voluntarios para la divulgaci\u00f3n de datos sobre sus impactos y medidas de mitigaci\u00f3n, contribuyendo as\u00ed a la transparencia y fortaleciendo el cumplimiento del art\u00edculo 6 del Acuerdo de Escaz\u00fa y la Ley 1712 de 2014 (Ley de Transparencia).", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Realizar un diagn\u00f3stico r\u00e1pido para identificar actores privados relevantes en el territorio y definir posibles temas ambientales sobre los cuales podr\u00edan compartir informaci\u00f3n. Esto servir\u00e1 como base para formular una estrategia inicial.", "points": 1}, "S\u00ed - Avanzado": {"text": "Revisar y actualizar la estrategia institucional para incluir mecanismos de evaluaci\u00f3n del impacto de la publicaci\u00f3n de informaci\u00f3n ambiental por privados, y fortalece la capacidad del equipo t\u00e9cnico para su implementaci\u00f3n.", "points": 3}, "S\u00ed - Intermedio": {"text": "Formalizar los lineamientos existentes en un documento gu\u00eda o protocolo interno que oriente c\u00f3mo involucrar a privados en la publicaci\u00f3n de informaci\u00f3n ambiental, y realiza al menos una acci\u00f3n piloto.", "points": 2}}'::json),
(21, 3, 'Tengo participación ciudadana abierta e inclusiva en las decisiones ambientales de manera transparente y teniendo en cuenta sus necesidades', 'statement', 21, '{"No": {"text": "Inicie la implementaci\u00f3n de espacios b\u00e1sicos de participaci\u00f3n, en coherencia con lo establecido por el Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n). Estos espacios deben permitir a la ciudadan\u00eda expresar sus necesidades y acceder de manera transparente a la toma de decisiones ambientales.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Defina y Fortalezca los espacios existentes para garantizar mayor inclusi\u00f3n, representatividad y acceso equitativo a la informaci\u00f3n. La participaci\u00f3n debe ser promovida activamente entre actores diversos del territorio, en cumplimiento de los principios de transparencia y no discriminaci\u00f3n del Acuerdo de Escaz\u00fa y la La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 1}, "S\u00ed - Avanzado": {"text": "A\u00f1ada nuevas estrategias de participaci\u00f3n, asegurando que sean incidentes en las decisiones ambientales y cuenten con enfoques diferenciales alineadas con el Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 3}, "S\u00ed - Intermedio": {"text": "Proponga mecanismos estructurados de retroalimentaci\u00f3n, trazabilidad de aportes ciudadanos y estrategias de comunicaci\u00f3n clara y accesible. Esto contribuye a una toma de decisiones ambientales m\u00e1s leg\u00edtima y alineada conel Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 2}}'::json),
(23, 3, 'Informo oportunamente a la ciudadanía sobre los detalles de la participación en los procesos de toma de decisiones ambientales (por medios físicos, remotos y locales de comunicación)', 'statement', 23, '{"No": {"text": "Establezca canales b\u00e1sicos de comunicaci\u00f3n que aseguren el acceso oportuno y claro a la informaci\u00f3n sobre procesos de participaci\u00f3n, conforme al principio de acceso a la informaci\u00f3n del Acuerdo de Escaz\u00fa y las disposiciones de la Ley 1712 de 2014 (Ley de Transparencia).", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Diversifique los medios de informaci\u00f3n utilizados, incluyendo formatos f\u00edsicos, digitales y comunitarios, para establecer una estrategia de difusi\u00f3n oportuna, clara y accesible que permita a diferentes p\u00fablicos conocer y acceder a los espacios de participaci\u00f3n. conforme al principio de acceso a la informaci\u00f3n del Acuerdo de Escaz\u00fa y las disposiciones de la Ley 1712 de 2014 (Ley de Transparencia).", "points": 1}, "S\u00ed - Avanzado": {"text": "Continue con la evaluaci\u00f3n peri\u00f3dica y la mejora continua de este sistema, garantizando su accesibilidad, pertinencia y adaptaci\u00f3n a las necesidades de la ciudadan\u00eda, cumpliendo plenamente con los principios del Acuerdo de Escaz\u00fa y y las disposiciones de la Ley 1712 de 2014 (Ley de Transparencia).", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca la estrategia de comunicaci\u00f3n para asegurar que toda la ciudadan\u00eda reciba informaci\u00f3n clara, en lenguaje accesible, con enfoque territorial y en tiempos adecuados. Esta pr\u00e1ctica contribuye al ejercicio efectivo del derecho a participar y al cumplimiento del Acuerdo de Escaz\u00fa y las disposiciones de la Ley 1712 de 2014 (Ley de Transparencia).", "points": 2}}'::json),
(25, 3, 'Informo oportunamente a los grupos directamente afectados sobre su derecho a participar en los proyectos de impacto ambiental', 'statement', 25, '{"No": {"text": "establezca mecanismos b\u00e1sicos para informar a los grupos directamente afectados sobre su derecho a participar en los proyectos de impacto ambiental. Esta informaci\u00f3n debe ser clara, accesible y proporcionada en momentos clave del proceso decisional, conforme al principio de acceso a la informaci\u00f3n del Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Mejore la difusi\u00f3n de la informaci\u00f3n, asegurando que todos los grupos directamente afectados conozcan de manera oportuna sus derechos de participaci\u00f3n. La informaci\u00f3n debe ser comunicada a trav\u00e9s de canales adecuados y accesibles, cumpliendo con los principios de equidad y transparencia establecidos en el Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 1}, "S\u00ed - Avanzado": {"text": "Continue consolidando estos procesos, promoviendo su sostenibilidad y evaluando continuamente la efectividad de los canales utilizados, para garantizar una participaci\u00f3n inclusiva y alineada con los principios del Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca los canales de comunicaci\u00f3n para asegurar que todos los grupos afectados reciban la informaci\u00f3n de manera clara, comprensible y accesible. Adem\u00e1s, se debe garantizar que la informaci\u00f3n llegue con suficiente antelaci\u00f3n, permitiendo la participaci\u00f3n informada en todas las etapas del proceso, como establece el Acuerdo de Escaz\u00fa y La Ley 1757 de 2015 (Ley de Participaci\u00f3n).", "points": 2}}'::json),
(27, 3, 'Informo oportunamente a la ciudadanía sobre la decisión ambiental final y los motivos que la sustentan', 'statement', 27, '{"No": {"text": "Revise la normativa vigente relacionada con el acceso a la informaci\u00f3n p\u00fablica, en especial la Ley 1712 de 2014, conocida como la Ley de Transparencia y del Derecho de Acceso a la Informaci\u00f3n P\u00fablica Nacional. En caso de que la entidad o territorio pertenezca a la Regi\u00f3n Metropolitana Bogot\u00e1-Cundinamarca, tambi\u00e9n debe considerarse la Ley 2199 de 2022, que establece disposiciones espec\u00edficas sobre gobernanza regional y acceso a la informaci\u00f3n. Adem\u00e1s, dado que Colombia es signataria y ha ratificado el Acuerdo de Escaz\u00fa, se debe cumplir con lo establecido en su articulado, que garantiza el derecho de acceso a la informaci\u00f3n, la participaci\u00f3n p\u00fablica y el acceso a la justicia en asuntos ambientales.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Mejore la forma en que se informa a la ciudadan\u00eda sobre las decisiones ambientales, pasando de una comunicaci\u00f3n b\u00e1sica a una m\u00e1s clara, accesible y detallada. Es importante explicar no solo la decisi\u00f3n, sino tambi\u00e9n sus fundamentos t\u00e9cnicos y jur\u00eddicos. Adem\u00e1s, se deben usar canales efectivos y comprensibles para todos los p\u00fablicos. Esto refuerza el cumplimiento de la Ley 1712 de 2014 y del Acuerdo de Escaz\u00fa. Una comunicaci\u00f3n m\u00e1s completa fortalece la transparencia y la participaci\u00f3n ciudadana.", "points": 1}, "S\u00ed - Avanzado": {"text": "Podr\u00eda en caso de no tenerlo elaborar una estrategia de comunicaci\u00f3n clara y multiformato que facilite la comprensi\u00f3n de las decisiones ambientales y sus fundamentos. En donde ademas se incluyan mecanismos de retroalimentaci\u00f3n ciudadana y evaluaci\u00f3n del impacto informativo. Esto refuerza el cumplimiento normativo y promueve una participaci\u00f3n ambiental efectiva. Lo que podr\u00eda posiicionar a la entidad como referente en transparencia y participaci\u00f3n ambiental efectiva.", "points": 3}, "S\u00ed - Intermedio": {"text": "Avance hacia un nivel m\u00e1s s\u00f3lido de divulgaci\u00f3n de las decisiones ambientales, complementando la informaci\u00f3n b\u00e1sica con explicaciones m\u00e1s claras y detalladas sobre los fundamentos t\u00e9cnicos, jur\u00eddicos y ambientales que las sustentan. Tambi\u00e9n se recomienda emplear formatos accesibles y canales efectivos para llegar a diversos p\u00fablicos. Esto permite fortalecer la transparencia institucional y dar cumplimiento a lo establecido en la Ley 1712 de 2014 y el Acuerdo de Escaz\u00fa, promoviendo una participaci\u00f3n ciudadana m\u00e1s informada y significativa.", "points": 2}}'::json),
(29, 3, 'Tengo criterios claros para criterios determinar qué personas o comunidades están directamente afectados por un proyecto o decisión ambiental', 'statement', 29, '{"No": {"text": "Defina criterios claros, p\u00fablicos y participativos para identificar a las personas o comunidades directamente afectadas, conforme al Art\u00edculo 5, 6 y 7 del Acuerdo de Escaz\u00fa. Esto permitir\u00e1 garantizar la participaci\u00f3n desde las primeras etapas del proceso, como lo exige la Ley 1757 de 2015, y asegurar el acceso a informaci\u00f3n relevante de acuerdo con la Ley 1712 de 2014, promoviendo transparencia y equidad en la gesti\u00f3n ambiental.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Inicie la definici\u00f3n de criterios objetivos y p\u00fablicos para identificar a las personas o comunidades directamente afectadas por decisiones ambientales, conforme al Art\u00edculo 5 y 6 del Acuerdo de Escaz\u00fa, que exige garantizar la participaci\u00f3n desde etapas tempranas. Esto debe complementarse con mecanismos b\u00e1sicos de consulta y acceso a la informaci\u00f3n, en l\u00ednea con la Ley 1712 de 2014 y la Ley 1757 de 2015.", "points": 1}, "S\u00ed - Avanzado": {"text": "Podria profundizar en la transparencia y trazabilidad de los procesos de identificaci\u00f3n de comunidades afectadas, haciendo p\u00fablicos los criterios y resultados, con validaci\u00f3n social y t\u00e9cnica. Conforme al Art\u00edculo 7 del Acuerdo de Escaz\u00fa, esto debe ir acompa\u00f1ado de una participaci\u00f3n informada, oportuna y continua, garantizando derechos diferenciales y fortaleciendo la confianza en la gesti\u00f3n ambiental.", "points": 3}, "S\u00ed - Intermedio": {"text": "Fortalezca y sistematice los criterios existentes, asegurando que sean aplicados de forma coherente, inclusiva y con enfoque territorial y diferencial, en cumplimiento del Art\u00edculo 5 y 6 del Acuerdo de Escaz\u00fa. Adem\u00e1s, se sugiere garantizar que los grupos identificados tengan acceso oportuno a informaci\u00f3n p\u00fablica, seg\u00fan Ley 1712 de 2014 y espacios de participaci\u00f3n efectiva, seg\u00fan Ley 1757 de 2015.", "points": 2}}'::json),
(33, 3, 'Diseño y ejecuto la audiencia de rendición de cuentas de mi entidad con el objetivo de que ciudadanía de mi territorio evalúe mi gestión ambiental y los resultados de la toma de decisiones ambientales', 'statement', 33, '{"No": {"text": "Dise\u00f1e e implemente un proceso formal de rendici\u00f3n de cuentas en el que la ciudadan\u00eda pueda evaluar la gesti\u00f3n ambiental de la entidad, de acuerdo con lo dispuesto en el Art. 8 de la Ley 1712 de 2014 y los principios de transparencia y participaci\u00f3n activa establecidos en la Ley 1757 de 2015. Este proceso debe garantizar que la informaci\u00f3n presentada sea clara, accesible y comprensible, especialmente para grupos vulnerables, y debe alinearse con los lineamientos del Acuerdo de Escaz\u00fa. Establecer un sistema de retroalimentaci\u00f3n ayudar\u00e1 a fortalecer la confianza y mejorar la toma de decisiones ambientales.", "points": 0}, "No aplica": {"text": "Revise en la normatividad vigente si en realidad esto no le aplica a su entidad y en la siguiente casilla justifique su elecci\u00f3n con base en el criterio jur\u00eddico de su entidad.", "points": 0}, "S\u00ed - B\u00e1sico": {"text": "Establezca un proceso formal de rendici\u00f3n de cuentas donde la ciudadan\u00eda pueda evaluar la gesti\u00f3n ambiental de la entidad, siguiendo lo establecido en la Ley 1712 de 2014 y los principios de participaci\u00f3n de la Ley 1757 de 2015. La audiencia debe garantizar que la informaci\u00f3n presentada sea clara, accesible y comprensible para todos los ciudadanos. Adem\u00e1s, se sugiere ofrecer espacios para que la comunidad pueda hacer preguntas y recibir respuestas de manera efectiva.", "points": 1}, "S\u00ed - Avanzado": {"text": "Fortalezca el proceso de rendici\u00f3n de cuentas alineado con los principios de transparencia, participaci\u00f3n activa y acceso a la informaci\u00f3n de la Ley 1757 de 2015, la Ley 1712 de 2014 y el Acuerdo de Escaz\u00fa. Este proceso debe integrar tecnolog\u00edas accesibles para facilitar la participaci\u00f3n ciudadana y garantizar la accesibilidad de la informaci\u00f3n. Adem\u00e1s, se sugiere iniciar la implementaci\u00f3n gradual de herramientas b\u00e1sicas de an\u00e1lisis de datos para evaluar la retroalimentaci\u00f3n recibida y mejorar la toma de decisiones ambientales, adapt\u00e1ndose a los recursos disponibles.", "points": 3}, "S\u00ed - Intermedio": {"text": "Formalice y estructure un proceso de rendici\u00f3n de cuentas que permita a la ciudadan\u00eda evaluar la gesti\u00f3n ambiental, en l\u00ednea con los principios de transparencia y participaci\u00f3n activa establecidos en la Ley 1757 de 2015 y la Ley 1712 de 2014. La audiencia debe ser inclusiva, garantizando accesibilidad y formatos comprensibles para todos los grupos, especialmente los vulnerables, tal como lo exige el Acuerdo de Escaz\u00fa. Asimismo, se debe incluir un mecanismo de retroalimentaci\u00f3n que permita la mejora continua en la toma de decisiones ambientales.", "points": 2}}'::json);
